package main

import (
	"context"
	"fmt"
	"github.com/techcraftlabs/mpesa"
	"log"
	"net/http"
	"os"
)

func main() {
	apiKey := "APwvV7m1UKSjHXquRZNBbW24HzC5SK31"
	//pubKey := ""

	pubKey := ""

	client := &mpesa.Client{
		Config:    &mpesa.Config{
			Name:            "paymonga",
			Version:         "1.0",
			Description:     "mongas",
			APIKey:          apiKey,
			PublicKey:       pubKey,
			SessionLifetime: 60,
			TrustedSources:  nil,
		},
		Http:      http.DefaultClient,
		BasePath:  "openapi.m-pesa.com",
		DebugMode: true,
		Logger:    os.Stderr,
	}
	fm := &mpesa.FormatAdapter{
		Next: client,
	}

	logger := log.New(os.Stderr, "MPESA",log.Ldate | log.Ltime | log.Lshortfile)
	lg := &mpesa.LoggerAdapter{
		Logger: logger,
		Next:   fm,
	}

	resp, err := lg.SessionID(context.Background(),mpesa.SANDBOX,*mpesa.TanzaniaMarket)
	if err != nil{
		log.Fatalln(err)
	}

	fmt.Printf("reponse error: %s\n", resp.OutputErr)
}
